/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `tr_fm_goods_update_view` AFTER UPDATE ON `fm_goods` FOR EACH ROW BEGIN
	SET @category_code = (SELECT CONCAT(GROUP_CONCAT(category_code),",") FROM fm_category_link WHERE goods_seq=NEW.goods_seq);
	SET @brand_code = (SELECT CONCAT(GROUP_CONCAT(category_code),",") FROM fm_brand_link WHERE goods_seq=NEW.goods_seq);
	IF NEW.goods_view <> OLD.goods_view THEN
		WHILE (LOCATE(',', @category_code) > 0) DO
			SET @value = SUBSTRING(@category_code,1, LOCATE(',',@category_code)-1); 
			SET @category_code= SUBSTRING(@category_code, LOCATE(',',@category_code) + 1); 
			IF NEW.goods_view = 'look' THEN
				INSERT INTO fm_count set kind = 'category', code = @value, totalcount = 1
				ON DUPLICATE KEY UPDATE totalcount = IFNULL(totalcount, 0) + 1;
			ELSEIF NEW.goods_view = 'notLook' THEN
				UPDATE fm_count SET totalcount = totalcount - 1 WHERE code = @value;
			END IF;
		END WHILE;
		WHILE (LOCATE(',', @brand_code) > 0) DO
			SET @value = SUBSTRING(@brand_code,1, LOCATE(',',@brand_code)-1); 
			SET @brand_code= SUBSTRING(@brand_code, LOCATE(',',@brand_code) + 1); 
			IF NEW.goods_view = 'look' THEN
				INSERT INTO fm_count set kind = 'brand', code = @value, totalcount = 1
				ON DUPLICATE KEY UPDATE totalcount = IFNULL(totalcount, 0) + 1;
			ELSEIF NEW.goods_view = 'notLook' THEN
				UPDATE fm_count SET totalcount = totalcount - 1 WHERE code = @value;
			END IF;
		END WHILE;
		DELETE FROM fm_count WHERE totalcount < 1;
	END IF;
END */;;
DELIMITER ;
