  `package_unit_ea5` int unsigned DEFAULT NULL COMMENT '단위수량5',
  `weight` float unsigned DEFAULT '0' COMMENT '옵션 개당무게 ( 단위 kg )',
  `option_view` enum('Y','N') COLLATE utf8mb4_unicode_ci DEFAULT 'Y' COMMENT '옵션 노출 여부(Y : 노출, N : 미노출)',
  `full_barcode` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '상품 전체 바코드-트리거로 처리',
