  KEY `code_seq` (`code_seq`),
  KEY `price` (`price`),
  KEY `option_type` (`option_type`),
  KEY `full_barcode` (`full_barcode`),
  KEY `fk_fm_goods_option_fm_goods` (`goods_seq`,`default_option`,`commission_type`,`commission_rate`)
) ENGINE=InnoDB AUTO_INCREMENT=154 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='상품옵션';
